import { EntityDefinition, Comparer, IdSelector, EntityAdapter } from '@reduxjs/toolkit/src/entities/models'
import { createInitialStateFactory } from '@reduxjs/toolkit/src/entities/entity_state'
import { createSelectorsFactory } from '@reduxjs/toolkit/src/entities/state_selectors'
import { createSortedStateAdapter } from '@reduxjs/toolkit/src/entities/sorted_state_adapter'
import { createUnsortedStateAdapter } from '@reduxjs/toolkit/src/entities/unsorted_state_adapter'

/**
 *
 * @param options
 *
 * @public
 */
export function createEntityAdapter<T>(
  options: {
    selectId?: IdSelector<T>
    sortComparer?: false | Comparer<T>
  } = {}
): EntityAdapter<T> {
  const { selectId, sortComparer }: EntityDefinition<T> = {
    sortComparer: false,
    selectId: (instance: any) => instance.id,
    ...options
  }

  const stateFactory = createInitialStateFactory<T>()
  const selectorsFactory = createSelectorsFactory<T>()
  const stateAdapter = sortComparer
    ? createSortedStateAdapter(selectId, sortComparer)
    : createUnsortedStateAdapter(selectId)

  return {
    selectId,
    sortComparer,
    ...stateFactory,
    ...selectorsFactory,
    ...stateAdapter
  }
}
